import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { AuthService } from './auth.service';

type LoginResponse = { accessToken: string };

@Component({
  selector: 'stm-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
})
export class LoginComponent {
  private readonly http = inject(HttpClient);
  private readonly router = inject(Router);
  private readonly auth = inject(AuthService);

  email = 'owner@demo.com';
  password = 'Owner123!';
  error = '';

  private readonly API = 'http://localhost:3000/api';

  submit() {
    this.error = '';

    this.http
      .post<LoginResponse>(`${this.API}/auth/login`, {
        email: this.email,
        password: this.password,
      })
      .subscribe({
        next: (res) => {
          this.auth.setToken(res.accessToken);
          this.router.navigateByUrl('/tasks');
        },
        error: () => {
          this.error = 'Login failed';
        },
      });
  }
}
<h1>Secure Task Manager</h1>

<h2>Sign in</h2>

<form (ngSubmit)="submit()">
  <label>
    Email
    <input name="email" [(ngModel)]="email" />
  </label>

  <label style="margin-left: 12px;">
    Password
    <input name="password" type="password" [(ngModel)]="password" />
  </label>

  <button style="margin-left: 12px;" type="submit">Sign in</button>
</form>

<p *ngIf="error" style="color: #b00020;">{{ error }}</p>

<h3>Demo accounts:</h3>
<p>owner&#64;demo.com / Owner123!</p>
<p>admin&#64;demo.com / Admin123!</p>
<p>viewer&#64;demo.com / Viewer123!</p>
