import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { FormsModule } from '@angular/forms';

type TaskStatus = 'OPEN' | 'IN_PROGRESS' | 'DONE';

type Task = {
  id: string;
  orgId: string;
  title: string;
  description?: string;
  status: TaskStatus;
  createdByEmail: string;
  createdAt: string;
  updatedAt: string;
};

@Component({
  selector: 'stm-tasks',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './tasks.component.html',
})
export class TasksComponent implements OnInit {
  private readonly API = 'http://localhost:3000/api';

  tasks: Task[] = [];
  loading = false;
  error = '';

  // create form
  title = '';
  description = '';
  status: TaskStatus = 'OPEN';

  constructor(private readonly http: HttpClient) {}

  ngOnInit() {
    this.load();
  }

  load() {
    this.loading = true;
    this.error = '';

    this.http.get<Task[]>(`${this.API}/tasks`).subscribe({
      next: (data) => {
        this.tasks = data;
        this.loading = false;
      },
      error: (err) => {
        this.loading = false;
        this.error = err?.error?.message ?? 'Failed to load tasks';
      },
    });
  }

  create() {
    this.error = '';

    this.http
      .post<Task>(`${this.API}/tasks`, {
        title: this.title,
        description: this.description,
        status: this.status,
      })
      .subscribe({
        next: (created) => {
          // reset form
          this.title = '';
          this.description = '';
          this.status = 'OPEN';

          // refresh list
          this.tasks = [created, ...this.tasks];
        },
        error: (err) => {
          this.error = err?.error?.message ?? 'Failed to create task';
        },
      });
  }
}
<h1>Tasks</h1>

<div *ngIf="loading">Loading...</div>
<p *ngIf="error" style="color: #b00020;">{{ error }}</p>

<hr />

<h2>Create Task</h2>

<div>
  <label>
    Title
    <input [(ngModel)]="title" name="title" />
  </label>
</div>

<div style="margin-top: 8px;">
  <label>
    Description
    <input [(ngModel)]="description" name="description" />
  </label>
</div>

<div style="margin-top: 8px;">
  <label>
    Status
    <select [(ngModel)]="status" name="status">
      <option value="OPEN">OPEN</option>
      <option value="IN_PROGRESS">IN_PROGRESS</option>
      <option value="DONE">DONE</option>
    </select>
  </label>
</div>

<button style="margin-top: 10px;" (click)="create()" [disabled]="!title">
  Create
</button>

<hr />

<h2>Task List</h2>

<div *ngIf="tasks.length === 0 && !loading">No tasks yet.</div>

<ul>
  <li *ngFor="let t of tasks">
    <b>{{ t.title }}</b>
    <span> — {{ t.status }}</span>
    <div *ngIf="t.description">{{ t.description }}</div>
    <small>Created by: {{ t.createdByEmail }}</small>
  </li>
</ul>
