import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { HttpClient, HttpHeaders } from '@angular/common/http';

type TaskStatus = 'OPEN' | 'IN_PROGRESS' | 'DONE';

type Task = {
  id: string;
  orgId: string;
  title: string;
  description?: string | null;
  status: TaskStatus;
  createdByEmail: string;
  createdAt: string;
  updatedAt: string;
};

@Component({
  selector: 'stm-tasks',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './tasks.component.html',
  styleUrl: './tasks.component.css',
})
export class TasksComponent implements OnInit {
  private readonly API = 'http://localhost:3000/api';

  tasks: Task[] = [];

  // form
  title = '';
  description = '';
  status: TaskStatus = 'OPEN';

  // ui state
  loading = false;
  creating = false;
  error = '';

  constructor(private readonly http: HttpClient) {}

  ngOnInit(): void {
    this.load();
  }

  /** used by the Refresh button */
  refresh(): void {
    this.load();
  }

  private authHeaders(): HttpHeaders {
    const token = localStorage.getItem('accessToken') ?? '';
    return new HttpHeaders({
      Authorization: `Bearer ${token}`,
    });
  }

  async load(): Promise<void> {
    this.error = '';
    this.loading = true;

    try {
      const headers = this.authHeaders();
      const res = await this.http
        .get<Task[]>(`${this.API}/tasks`, { headers })
        .toPromise();

      this.tasks = res ?? [];
    } catch (e: any) {
      // If token missing/expired, backend returns 401
      this.error = e?.status === 401 ? 'Unauthorized (please login again).' : 'Failed to load tasks.';
      this.tasks = [];
    } finally {
      this.loading = false;
    }
  }

  async create(): Promise<void> {
    this.error = '';

    const title = this.title.trim();
    if (!title) return;

    this.creating = true;

    try {
      const headers = this.authHeaders();
      const created = await this.http
        .post<Task>(
          `${this.API}/tasks`,
          {
            title,
            description: this.description.trim() || undefined,
            status: this.status,
          },
          { headers }
        )
        .toPromise();

      if (created) {
        // optimistic add to top
        this.tasks = [created, ...this.tasks];
      }

      // reset form
      this.title = '';
      this.description = '';
      this.status = 'OPEN';
    } catch (e: any) {
      this.error = e?.status === 401 ? 'Unauthorized (please login again).' : 'Failed to create task.';
    } finally {
      this.creating = false;
    }
  }
}
