import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { TasksClient, Task } from './tasks.service';
import { AuthService } from '../auth/auth.service';
import { RouterModule } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'stm-tasks',
  standalone: true,
  imports: [CommonModule, RouterModule, FormsModule],
  templateUrl: './tasks.component.html',
})
export class TasksComponent implements OnInit {
  tasks$: Observable<Task[]>;
  newTitle = '';
  newDescription = '';
  loading = false;

  constructor(public tasks: TasksClient, public auth: AuthService) {
    this.tasks$ = this.tasks.list();
  }

  ngOnInit() {
    this.reload();
  }

  reload() {
    this.tasks$ = this.tasks.list();
  }

  canWrite() {
    return this.auth.hasRole(['OWNER', 'ADMIN']);
  }

  async create() {
    if (!this.newTitle) return;
    this.loading = true;
    try {
      await this.tasks.create({
        title: this.newTitle,
        description: this.newDescription,
        status: 'OPEN',
      }).toPromise();
      this.newTitle = '';
      this.newDescription = '';
      this.reload();
    } catch (e) {
      console.error(e);
      alert('Create failed');
    } finally {
      this.loading = false;
    }
  }

  async remove(id: string) {
    if (!confirm('Delete this task?')) return;
    try {
      await this.tasks.delete(id).toPromise();
      this.reload();
    } catch (e) {
      console.error(e);
      alert('Delete failed');
    }
  }

  async setStatus(t: Task, status: Task['status']) {
    try {
      await this.tasks.update(t.id, { status }).toPromise();
      this.reload();
    } catch (e) {
      console.error(e);
      alert('Update failed');
    }
  }
}
