import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { HttpClient, HttpHeaders } from '@angular/common/http';

type Role = 'OWNER' | 'ADMIN' | 'VIEWER';
type TaskStatus = 'OPEN' | 'IN_PROGRESS' | 'DONE';

type Task = {
  id: string;
  orgId: string;
  title: string;
  description?: string | null;
  status: TaskStatus;
  createdByEmail: string;
  createdAt: string;
  updatedAt: string;
};

type JwtPayload = {
  sub: string;
  email: string;
  role: Role;
  orgId: string;
  iat: number;
  exp: number;
};

@Component({
  selector: 'stm-tasks',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './tasks.component.html',
  styleUrl: './tasks.component.css',
})
export class TasksComponent implements OnInit {
  private readonly API = 'http://localhost:3000/api';

  tasks: Task[] = [];

  // create form
  title = '';
  description = '';
  status: TaskStatus = 'OPEN';

  // ui state
  loading = false;
  creating = false;
  error = '';

  // role + user
  me: JwtPayload | null = null;

  // editing state
  editingId: string | null = null;
  editTitle = '';
  editDescription = '';
  editStatus: TaskStatus = 'OPEN';
  savingId: string | null = null;
  deletingId: string | null = null;

  constructor(private readonly http: HttpClient) {}

  ngOnInit(): void {
    this.me = this.decodeToken();
    this.load();
  }

  get canWrite(): boolean {
    return this.me?.role === 'OWNER' || this.me?.role === 'ADMIN';
  }

  refresh(): void {
    this.load();
  }

  private authHeaders(): HttpHeaders {
    const token = localStorage.getItem('accessToken') ?? '';
    return new HttpHeaders({ Authorization: `Bearer ${token}` });
  }

  private decodeToken(): JwtPayload | null {
    const token = localStorage.getItem('accessToken');
    if (!token) return null;

    try {
      const middle = token.split('.')[1];
      const json = atob(middle.replace(/-/g, '+').replace(/_/g, '/'));
      return JSON.parse(json) as JwtPayload;
    } catch {
      return null;
    }
  }

  async load(): Promise<void> {
    this.error = '';
    this.loading = true;

    try {
      const headers = this.authHeaders();
      const res = await this.http.get<Task[]>(`${this.API}/tasks`, { headers }).toPromise();
      this.tasks = res ?? [];
    } catch (e: any) {
      this.error = e?.status === 401 ? 'Unauthorized (please login again).' : 'Failed to load tasks.';
      this.tasks = [];
    } finally {
      this.loading = false;
    }
  }

  async create(): Promise<void> {
    this.error = '';
    if (!this.canWrite) {
      this.error = 'You do not have permission to create tasks.';
      return;
    }

    const title = this.title.trim();
    if (!title) return;

    this.creating = true;

    try {
      const headers = this.authHeaders();
      const created = await this.http
        .post<Task>(
          `${this.API}/tasks`,
          {
            title,
            description: this.description.trim() || undefined,
            status: this.status,
          },
          { headers }
        )
        .toPromise();

      if (created) this.tasks = [created, ...this.tasks];

      this.title = '';
      this.description = '';
      this.status = 'OPEN';
    } catch (e: any) {
      this.error = e?.status === 401 ? 'Unauthorized (please login again).' : 'Failed to create task.';
    } finally {
      this.creating = false;
    }
  }

  startEdit(t: Task): void {
    if (!this.canWrite) return;

    this.editingId = t.id;
    this.editTitle = t.title ?? '';
    this.editDescription = (t.description ?? '') as string;
    this.editStatus = t.status;
  }

  cancelEdit(): void {
    this.editingId = null;
    this.editTitle = '';
    this.editDescription = '';
    this.editStatus = 'OPEN';
  }

  async saveEdit(id: string): Promise<void> {
    this.error = '';
    if (!this.canWrite) {
      this.error = 'You do not have permission to edit tasks.';
      return;
    }

    const title = this.editTitle.trim();
    if (!title) {
      this.error = 'Title cannot be empty.';
      return;
    }

    this.savingId = id;

    try {
      const headers = this.authHeaders();
      const updated = await this.http
        .patch<Task>(
          `${this.API}/tasks/${id}`,
          {
            title,
            description: this.editDescription.trim() || undefined,
            status: this.editStatus,
          },
          { headers }
        )
        .toPromise();

      if (updated) {
        this.tasks = this.tasks.map((t) => (t.id === id ? updated : t));
      }

      this.cancelEdit();
    } catch (e: any) {
      this.error =
        e?.status === 401
          ? 'Unauthorized (please login again).'
          : e?.status === 403
          ? 'Forbidden (role does not allow editing).'
          : 'Failed to update task.';
    } finally {
      this.savingId = null;
    }
  }

  async remove(id: string): Promise<void> {
    this.error = '';
    if (!this.canWrite) {
      this.error = 'You do not have permission to delete tasks.';
      return;
    }

    const ok = confirm('Delete this task?');
    if (!ok) return;

    this.deletingId = id;

    try {
      const headers = this.authHeaders();
      await this.http.delete(`${this.API}/tasks/${id}`, { headers }).toPromise();
      this.tasks = this.tasks.filter((t) => t.id !== id);
    } catch (e: any) {
      this.error =
        e?.status === 401
          ? 'Unauthorized (please login again).'
          : e?.status === 403
          ? 'Forbidden (role does not allow deleting).'
          : 'Failed to delete task.';
    } finally {
      this.deletingId = null;
    }
  }
}
